<?php/** * Created by Viktor Lazhevskyi. * User: viktor.lazhevskyi@gmail.com * Date: 02.02.2019 * Time: 12:25 */include_once __DIR__ .DS . "CostStrategy.php";abstract class Lesson{    private $duration;    private $costStrategy;    public function __construct($duration, CostStrategy $strategy)    {        $this->duration = $duration;        $this->costStrategy = $strategy;    }    public function cost()    {        return $this->costStrategy->cost($this);    }    public function chargeType()    {       return $this->costStrategy->chargeType();    }    public function getDuration(){        return $this->duration;    }}