<?php/** * Created by Viktor Lazhevskyi. * User: viktor.lazhevskyi@gmail.com * Date: 03.02.2019 * Time: 14:15 */abstract class ApptEncoder{    abstract function encode();}class BloggsApptEncoder extends ApptEncoder{    public function encode()    {        return "The data was encoded with BloggsCal \n";    }}class MegaApptEncoder extends ApptEncoder{    public function encode()    {        return "The data was encoded with MegaCal \n";    }}class CommsManager{    const BLOGGS = 1;    const MEGA = 2;    private $mode = 1;    function __construct($mode)    {        $this->mode = $mode;    }    function getApptEncoder()    {        switch ($this->mode) {            case 2:                return new MegaApptEncoder();                break;            default:                return new BloggsApptEncoder();        }    }}$comms  = new CommsManager(CommsManager::MEGA);$apptEncoder = $comms->getApptEncoder();print $apptEncoder->encode();