<?php/** * Created by Viktor Lazhevskyi. * User: viktor.lazhevskyi@gmail.com * Date: 03.02.2019 * Time: 14:18 */abstract class ApptEncode{    abstract function encode();}class BloggsApptEncode extends ApptEncode{    public function encode()    {        return "The data is encoded with BloggsCal \n";    }}abstract class CommsManager{    abstract function getHeaderText();    abstract function getApptEncode();    abstract function getFooterText();}class BloggsCommsManager extends CommsManager{    public function getHeaderText()    {        return "BloggsCal header\n";    }    public function getApptEncode()    {        return new BloggsApptEncode();    }    public function getFooterText()    {        return "BloggsCal footer \n";    }}$mgr = new BloggsCommsManager();print $mgr->getHeaderText();print $mgr->getApptEncode()->encode();print $mgr->getFooterText();